mapper.select:
select 

from MEDREC.MR_FILE_INDEX_TEMP
where PATIENT_ID = #{patientId,jdbcType=VARCHAR}
  and VISIT_ID = #{visitId,jdbcType=DECIMAL}
  and FILE_NO = #{fileNo,jdbcType=DECIMAL}

mapper.delete:
delete from MEDREC.MR_FILE_INDEX_TEMP
where PATIENT_ID = #{patientId,jdbcType=VARCHAR}
  and VISIT_ID = #{visitId,jdbcType=DECIMAL}
  and FILE_NO = #{fileNo,jdbcType=DECIMAL}
