mapper.select:
select 

from OUTPBILL.OUTP_BILL_ITEMS
where VISIT_DATE = #{visitDate,jdbcType=DATE}
  and VISIT_NO = #{visitNo,jdbcType=DECIMAL}
  and ITEM_NO = #{itemNo,jdbcType=DECIMAL}

mapper.delete:
delete from OUTPBILL.OUTP_BILL_ITEMS
where VISIT_DATE = #{visitDate,jdbcType=DATE}
  and VISIT_NO = #{visitNo,jdbcType=DECIMAL}
  and ITEM_NO = #{itemNo,jdbcType=DECIMAL}
