mapper.select:
select

from OUTPDOCT.OUTP_ORDERS_COSTS
where SERIAL_NO = #{serialNo,jdbcType=VARCHAR}
  and ORDER_CLASS = #{orderClass,jdbcType=VARCHAR}
  and ORDER_NO = #{orderNo,jdbcType=DECIMAL}
  and ITEM_NO = #{itemNo,jdbcType=DECIMAL}

select

from OUTPDOCT.OUTP_ORDERS_COSTS o
where visit_date =#{1}
  and  visit_no=#{2}
  and  PATIENT_ID=#{0}
  and ORDERED_BY_DEPT='s160'
  and  PERFORMED_BY !='0819'
and CHARGE_INDICATOR=0

select
MAX(o.item_no) ITEM_NO
from OUTPDOCT.OUTP_ORDERS_COSTS o
where  visit_date =#{0}
and  visit_no=#{1}
and SERIAL_NO = #{2}
and  PERFORMED_BY !='0819'
and CHARGE_INDICATOR=0


select
MAX(o.AMOUNT) AMOUNT
from OUTPDOCT.OUTP_ORDERS_COSTS o
where  visit_date =#{0}
and  visit_no=#{1}
and  PERFORMED_BY !='0819'
and ITEM_CLASS ='A'
and ADMINISTRATION =#{2}
and PATIENT_ID=#{3}
and CHARGE_INDICATOR=0

select
max(o.days) DAYS
from outpdoct.outp_orders_costs o
where order_class = 'A'
and visit_date =#{0}
and  visit_no=#{1}
and  PATIENT_ID=#{2}
and ADMINISTRATION = #{3}
and  PERFORMED_BY !='0819'
and CHARGE_INDICATOR=0

select
*
from outpdoct.outp_orders_costs o
where  visit_date =#{0}
and  visit_no=#{1}
and  PATIENT_ID=#{2}
and ITEM_CODE='120400007B'
and CHARGE_INDICATOR=0

select
*
from outpdoct.outp_orders_costs o
where  visit_date =#{0}
and  visit_no=#{1}
and  PATIENT_ID=#{2}
and ITEM_CODE='120400006'
and CHARGE_INDICATOR=0

select
*
from outpdoct.outp_orders_costs o
where order_class = 'A'
and visit_date =#{0}
and  visit_no=#{1}
and  PATIENT_ID=#{2}
and  ADMINISTRATION = #{3}
and  PERFORMED_BY !='0819'
and CHARGE_INDICATOR=0

select
*
from outpdoct.outp_orders_costs o
where  visit_date =#{1}
and  visit_no=#{0}
and  PATIENT_ID=#{2}
and item_code='71AAF01508'

mapper.delete:
delete from OUTPDOCT.OUTP_ORDERS_COSTS
where SERIAL_NO = #{serialNo,jdbcType=VARCHAR}
  and ORDER_CLASS = #{orderClass,jdbcType=VARCHAR}
  and ORDER_NO = #{orderNo,jdbcType=DECIMAL}
  and ITEM_NO = #{itemNo,jdbcType=DECIMAL}
